    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        double valorpagamento ,precol ,quantidade;
        valorpagamento = Double.parseDouble(jTxtvalorpagamento.getText());
        precol = Double.parseDouble(jTxtprecol.getText());
        quantidade = valorpagamento / precol;
        jTxtquantidade.setText(Double.toString(quantidade));
    }//GEN-LAST:event_jButton1ActionPerformed

